import { mapState, mapMutations } from "vuex";
import { ApiList } from "../../api/api";
import { currentcy } from "@/config/number";

export default {
  name: "FeeRateManager",
  data() {
    return {
      ruleForm: {
        card: "",
        cardhold: "",
        feeRate: []
      },
      currentUser: {},
      rules: {},
      cards: [],
      feeRate: []
    };
  },
  created() {
    let api = new ApiList();
    api.getAdminList().then(resp => {
      this.cards = resp.data.data;
    });
  },
  methods: {
    ...mapMutations(["remove_editableTabs"]),
    changeFee(item) {
      let api = new ApiList();
      api.changeFee(item).then(resp => {
          if (parseInt(resp.data.code) == 0) {
            this.$message({
              type: "success",
              message: "修改成功！"
            });
          } else {
            this.$message.error(resp.data.data.message);
          }
        })
        .catch(error => {
          console.log(error);
          this.$message.error("提交失败");
        });
    },
    deleteFee(item) {
      let api = new ApiList();
      api.deleteFee(item).then(resp => {
          console.log(resp);
          if (parseInt(resp.data.code) == 0) {
            this.$message({
              type: "success",
              message: "删除成功"
            });
            item.fee = null;
          } else {
            this.$message.error(resp.data.data.message);
          }
        })
        .catch(error => {
          console.log(error);
          this.$message.error("提交失败");
        });
    },
    changeCard(value) {
      let api = new ApiList();
      let user = {};
      this.cards.map(item => {
        item.username == value ? (user = item) : null;
      });
      // let userInfo = sessionStorage.getItem("userInfo");
      //   let params = {
      //     userId: JSON.parse(userInfo).id
      //   }
      api.searchFeeRate({userId: user.id}).then(data => {
        this.feeRate = data.data.data;
        this.feeRate.map(item => {
          item.userid = user.id;
        });
      });
      this.currentUser = user;
    },
    submitForm(type) {
      if (this.feeRate.length == 0) {
        this.$message.error("请选择商户!");
        return;
      }
      this.$refs.applyForm.validate(valid => {
        if (valid) {
          let apiList = new ApiList();
          apiList
            .feeAdd({ data: this.feeRate })
            .then(resp => {
              console.log(resp);
              if (parseInt(resp.data.code) == 0) {
                this.$message({
                  type: "success",
                  message: resp.data.data.message
                });
              } else {
                this.$message.error(resp.data.data.message);
              }
            })
            .catch(error => {
              console.log(error);
              this.$message.error("提交失败");
            });
        } else {
          console.log("error submit!!");
          return false;
        }
      });
    },
    seeSurplus() {
      let api = new ApiList();
      api.seeBalance().then(resp => {
        // this.ruleForm.curent = '1323';
        // this.ruleForm.curent = resp.data[0].balance;
        this.ruleForm.money = resp.data[0].balance;
      });
    }
  }
};
